import {Avatar, Box, Button, Drawer, DrawerBody, DrawerContent, DrawerHeader, DrawerOverlay, Input, Menu, MenuButton, MenuDivider, MenuItem, MenuList, Spinner, Text, Toast, Tooltip, useDisclosure, useToast, } from '@chakra-ui/react';
import React, {useState} from 'react';
import {BellIcon, ChevronDownIcon} from '@chakra-ui/icons';
import {ChatState} from '../../context/chatProvider';
import ProfileModel from './ProfileModel';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import ChatLoading from '../ChatLoading';
import UserListItem from '../UserAvatar/UserListItem';
import {getSender} from '../../config/ChatLogics';
import {Effect} from 'react-notification-badge';
import NotificationBatch from "react-notification-badge"


const SideDrawer = () => {

    let [search, setSearch]= useState("")
    let [searchResult, setSearchResult]= useState([])
    let [loading, setLoading]= useState(false)
    let [loadingChat, setLoadingChat]= useState()

    const { isOpen, onOpen, onClose } = useDisclosure()

    const {user, setSelectedChat, chats, setChats ,notification, setNotification} = ChatState();
    const navigation = useNavigate()
    const toast = useToast()

    const logoutHandler = () => {
        localStorage.removeItem("userInfo");
        navigation("/")
    }

    const accessChat = async (userId) => {
        try {
            setLoading(true)
            const config = {
                headers: {
                    "Content-type":"application/json",
                    Authorization:`Bearer ${user.token}`,
                }
            }
            const {data} = await axios.post(`/api/chat`,{userId},config)
            
            if(chats && !chats.find((c)=> c._id === data._id)) setChats([data, ...chats])

            setSelectedChat(data);
            setLoadingChat(false);
            onClose();
        } catch (error) {
            toast({
                title:"Error Occured!",
                description:error.message,
                status:"error",
                duration:5000,
                isClosable:true,
                position: "bottom-left"
            });
            return
        }
    }

    const handleSearch = async() => {
        if(!search) {
            toast({
                title:" Please Enter something in search",
                status:"warning",
                duration:5000,
                isClosable:true,
                position: "top-left"
            });
            return
        }

        try {
            setLoading(true)

            const config = {
                headers: {
                    Authorization:`Bearer ${user.token}`,
                }
            }

            const {data} = await axios.get(`/api/user?search=${search}`,config)
            setLoading(false);
            setSearchResult(data)

        } catch (error) {
            toast({
                title:"Error Occured!",
                description:error.message,
                status:"error",
                duration:5000,
                isClosable:true,
                position: "bottom-left"
            });
            return
        }
    }

    return (
        <>
            <Box display="flex" justifyContent="space-between" alignItems="center" w="100%" bg="white" p="5px 10px 5px 10px" borderWidth="5px">
                <Tooltip label="Search users to chat" hasArrow placement='bottom-end'>
                    <Button variant="ghost" onClick={onOpen}>
                        <i className='fas fa-search'></i>
                        <Text d={{base:"none", md:"flex"}} px="4"> Search User</Text>
                    </Button>
                </Tooltip>
                <Text fontSize="2xl" fontFamily="work sans" >Chatter Box</Text>
                <div>
                    <Menu>
                        <MenuButton p={1} >
                            <NotificationBatch
                                count={notification.length}
                                effect={Effect.SCALE}
                            />
                            <BellIcon fontSize="2xl" m={1} />
                        </MenuButton>
                        <MenuList pl={2}>
                            {!notification.length && "no new messages"}
                            {notification.map(notify => (
                                <MenuItem key={notify._id} onClick={()=> {
                                    setSelectedChat(notify.chat)
                                    setNotification(notification.filter((n) => n != notify))
                                }}>
                                    {notify.chat.isGroupChat? `New Message in ${notify.chat.chatName}`:`New Message from ${getSender(user, notify.chat.users)}`}
                                </MenuItem>
                            ))}
                        </MenuList>
                    </Menu>
                    <Menu>
                        <MenuButton as={Button} rightIcon={<ChevronDownIcon />} >
                            <Avatar size="sm" cursor="pointer" name={user.name} src={user.pic} />
                        </MenuButton>
                        <MenuList>
                            <ProfileModel user={user}>
                                {/* <MenuItem>My Profile</MenuItem> */}
                            </ProfileModel>
                            <MenuDivider />
                            <MenuItem onClick={logoutHandler}>Logout</MenuItem>
                        </MenuList>
                    </Menu>
                </div>
            </Box>
            <Drawer placement='left' onClose={onClose} isOpen={isOpen} >
                <DrawerOverlay />
                <DrawerContent>
                    <DrawerHeader borderBottomWidth="1px" > Search Users </DrawerHeader>
                    <DrawerBody>
                        <Box display="flex" pb={2}>
                            <Input
                                placeholder='Search By name or email'
                                mr={2}
                                value={search}
                                onChange={(e)=> setSearch(e.target.value)}
                            />
                            <Button onClick={handleSearch} >Go</Button>
                        </Box>
                        {loading? (
                            <ChatLoading />
                        ):
                        (
                            searchResult?.map(user => (
                                <UserListItem
                                    key={user._id}
                                    user={user}
                                    handleFunction={()=> accessChat(user._id)}
                                />
                            ))
                        )}
                        {loading && <Spinner ml="auto" display="flex" />}
                    </DrawerBody>
                </DrawerContent>
                
            </Drawer>
        </>
    );
}

export default SideDrawer;
